import React from 'react';
import './Styles.css';
import Slide from './Slide'
import bg from './assets/1.jpg'

export default class Slider extends React.Component {
  constructor (props) {
    super(props)
    this.data = [
      {
        bgColor: 'red',
        content: (
          <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100%'}}>
            Hello!<br/>This is a simple demonstration of slider content</div>
        )
      },
      {
        bgColor: 'blue',
        content: (
          <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100%'}}>
          <h1>Simply add an H1 tag in the content!</h1></div>
        )
      },
      {
        bgColor: 'green',
        content: (
          <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100%'}}>
          <h2>Yes! You should play around with styles...</h2></div>
        )
      },
      {
        bgColor: 'orange',
        content: (
          <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100%'}}>
          <p>And you can!!!</p></div>
        )
      },
      {
        bgImage: bg,
        content: (
          <div style={{display: 'flex', alignItems: 'flex-start', justifyContent: 'center', height: '100%', color: 'white'}}>
          <p>Here is a case of using a background image initially...</p></div>
        )
      },
      {
        bgColor: 'brown',
        content: (
          <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100%'}}>
          <span>Technically I could include a button here, but I put a SPAN!</span></div>
        )
      }
    ]

    this.state = {
        transformSpeed: 1,
        currentSlide: {
          slide: 1,
          transform: -360
        },
    }

    this.nextSlide = this.nextSlide.bind(this)
    this.prevSlide = this.prevSlide.bind(this)
    this.dotClick = this.dotClick.bind(this)  
    this.sliderMouseEnter = this.sliderMouseEnter.bind(this)
    this.sliderMouseLeave = this.sliderMouseLeave.bind(this)  
    this.handleLastSlide = this.handleLastSlide.bind(this)

    this.handleAutoplay()
  }

  handleLastSlide () {
    this.setState({
      transformSpeed: 0,
      currentSlide: {
        slide: 1,
        transform: -360
      }
    })
  }

  nextSlide () {
    let sliderContainer = document.getElementById('slides-container')
    this.setState({ 
      transformSpeed: 1
    })

    if (this.state.currentSlide.slide === this.data.length) {
      this.setState({ 
        currentSlide: {
          slide: 1,
          transform: this.state.currentSlide.transform -360
        }
      })
      sliderContainer.addEventListener('transitionend', this.handleLastSlide)
    } else {
      this.setState({ 
        currentSlide: {
          slide: this.state.currentSlide.slide + 1,
          transform: this.state.currentSlide.transform -360
        }
      })
    }
  }

  prevSlide () {
    if (this.state.currentSlide === 0) {
      this.setState({ currentSlide: this.data.length - 1})
    } else {
      this.setState({ currentSlide: this.state.currentSlide - 1})
    }
  }

  dotClick (event) {
    let currentDot = parseInt(event.currentTarget.getAttribute('data-dot-index'));
    this.setState({ currentSlide: currentDot})
  }

  getDots () {
    let dotsContent = [];
    for (let i = 0; i < this.data.length; i++) {
      let dotClassName = "dot";
      if (i === this.state.currentSlide) {
          dotClassName += " active-dot";
      }
      dotsContent.push(<button className={dotClassName} data-dot-index={i} key={`dot-${i}`} onClick={this.dotClick}></button>)
    }
    return dotsContent;
  }

  getSlide () {
    let slides = [];
    let slideArray = this.data

    slides.push(
      <Slide
          key={'DuplicatedLastSlide'}
          id={'DuplicatedLastSlide'}
          bgImage={slideArray[slideArray.length - 1].bgImage}
          bgColor={slideArray[slideArray.length - 1].bgColor}
          content={slideArray[slideArray.length - 1].content}
        />
    )

    slideArray.forEach((item, index) => {
      slides.push(
        <Slide
          key={`slide-${index}`}
          bgImage={item.bgImage}
          bgColor={item.bgColor}
          content={item.content}
        />
      )
    })

    slides.push(
      <Slide
          key={'DuplicatedFirstSlide'}
          id={'DuplicatedFirstSlide'}
          bgImage={slideArray[0].bgImage}
          bgColor={slideArray[0].bgColor}
          content={slideArray[0].content}
        />
    )

    return slides;
  }

  handleAutoplay () {
    if (this.props.autoplay) {
      this.slideInterval = setInterval(this.nextSlide, this.props.autoplayTime);
    }
  }

  sliderMouseEnter () {
    clearInterval(this.slideInterval);
  }

  sliderMouseLeave () {
    if (this.props.autoplay) {
      this.slideInterval = setInterval(this.nextSlide, this.props.autoplayTime);
    }
  }

  render () {
    let buttonContainer = null
    if (this.props.buttons) {
      buttonContainer = (
        <div className="buttons">
          <i className="fas fa-arrow-right nextBtn" onClick={this.nextSlide}></i>
          <i className="fas fa-arrow-left prevBtn" onClick={this.prevSlide}></i>
        </div>
      )
    }
    let dotContainer = null
    if (this.props.dots) {
      dotContainer = <div className="dots" key="dots">{this.getDots()}</div>
    }

    return (
      <div className="slider" onMouseEnter={this.sliderMouseEnter} onMouseLeave={this.sliderMouseLeave}>
        <div className="slides-container" id="slides-container" style={{transition: `transform ${this.state.transformSpeed}s ease-in-out`, transform: `translateX(${this.state.currentSlide.transform}px)`}}>
          {this.getSlide()}
        </div>
        {buttonContainer}
        {dotContainer}
      </div>
    )
  }
};
